SET EMAIL_REFRESH_TOKEN = #{refreshToken}
WHERE EMAIL_ID = #{emailId}

mapper.select:
SELECT COUNT(*) 
FROM MIMIR_USER 
WHERE EMAIL_ID = #{emailId}

mapper.update:
