SET EMAIL_REFRESH_TOKEN = NULL
WHERE EMAIL_ID = #{emailId}

UPDATE MIMIR_USER
SET LAST_LOGIN_TIME = SYSDATE
WHERE EMAIL_ID = #{emailId}

mapper.select:
