FROM MIMIR_USER
WHERE USER_NAME = #{name} AND EMAIL_ID = #{email}

SELECT *
FROM MIMIR_USER
WHERE EMAIL_ID = #{userId} AND USER_NAME = #{name} AND EMAIL_ID = #{email}

