mapper.select:
SELECT MENU_NAME, DESCRIPTION, PRICE
FROM REP_MENU
WHERE DINING_ID = #{diningId}

mapper.delete:
DELETE FROM DINING_IMAGE
WHERE DINING_ID = #{diningId}
  AND IMAGE_URL = #{imageUrl}
  AND IMAGE_TYPE = #{imageType}

mapper.update:
UPDATE DINING_IMAGE
SET DISPLAY_ORDER = #{displayOrder}
WHERE DINING_ID = #{diningId}
  AND IMAGE_URL = #{imageUrl}
  AND IMAGE_TYPE = 'CAROUSEL'
