mapper.select:
SELECT TIME_SLOT
FROM DINING_TIME_CONFIG
WHERE DINING_ID = #{diningId}
  AND MEAL_TYPE = #{mealType}
ORDER BY TIME_SLOT

mapper.update:
UPDATE DINING_TIME_CONFIG
SET TIME_SLOT = #{timeSlot}
WHERE CONFIG_ID = #{configId}

mapper.delete:
DELETE FROM DINING_TIME_CONFIG
WHERE DINING_ID = #{diningId}
  AND MEAL_TYPE = #{mealType}
